mapper.select:

WHERE USER_ID = #{user_id}
  AND FIL_CMPLT = '1'
  AND FIL_DLT = '0'


		 WHERE USER_ID = #{user_id}
		   AND FIL_DLT = '0'
 	       AND TODO_DDLN  < TRUNC(SYSDATE) -- 마감일 이전에


WHERE USER_ID = #{user_id}
  AND FIL_DLT = '0'
  AND TRUNC(TODO_DDLN) = TRUNC(SYSDATE) -- 마감일 오늘


		 WHERE USER_ID = #{user_id}
		   AND FIL_DLT = '0'
 	       AND TRUNC(TODO_DDLN)  > TRUNC(SYSDATE) -- 마감일 나중에
