mapper.insert:

	SELECT TODO_SEQ.nextval FROM DUAL

INSERT ALL
		  	 INTO TODO
					   ( TODO_IDX
					   , USER_ID
					   , TODO_TITLE
					   , TODO_DDLN
					   , CREATED_TIME
					   )
				VALUES ( #{todo_idx}
					   , #{user_id}
					   , #{todo_title}
					   , #{todo_ddln, jdbcType=DATE}
					   , #{created_time}
					   )
		   INTO FILTERS
		   			   ( TODO_IDX
		   			   , USER_ID
		   			   , FIL_TDY
		   			   , FIL_IMP
		   			   , FIL_CMPLT
		   			   , FIL_DLT
		   			   )
		   	 	VALUES (
		   	 			 #{todo_idx}
		   	 		   , #{user_id}
						, #{fil_tdy}
						, 0
						, #{fil_imp}
						, 0
						, #{fil_cmplt}
						, 0			
		   	 		   , 0
		   	 		   )
		   	 	SELECT * FROM DUAL

mapper.update:
UPDATE FILTERS
   SET FIL_DLT = '1'
 WHERE TODO_IDX = #{todo_idx}

UPDATE TODO
   SET DELETED_TIME = #{deleted_time}
 WHERE TODO_IDX = #{todo_idx}

	UPDATE TODO

        TODO_TITLE = #{todo_title},
        TODO_MEMO = #{todo_memo},
        TODO_DDLN = #{todo_ddln, jdbcType=DATE}

	 WHERE TODO_IDX = #{todo_idx}
	   AND USER_ID = #{user_id}

UPDATE FILTERS
FIL_TDY = #{fil_tdy}, FIL_IMP = #{fil_imp}, FIL_CMPLT = #{fil_cmplt}, FIL_DLT = #{fil_dlt},
WHERE TODO_IDX = #{todo_idx}

UPDATE TODO
   SET UPDATED_TIME = #{updated_time}
 WHERE TODO_IDX = #{todo_idx}

mapper.select:

	 WHERE USER_ID = #{user_id}
	   AND FIL_DLT = '0'
  ORDER BY CREATED_TIME ASC


WHERE USER_ID = #{user_id}
  AND FIL_DLT = '0'
ORDER BY CREATED_TIME DESC


WHERE USER_ID = #{user_id}
  AND TODO_TITLE LIKE '%'||#{todo_title}||'%'
ORDER BY CREATED_TIME DESC
